module.exports = function(gulp, plugins, config) {
   
   // Move Unbundled JS Library Files To Built Directory

    gulp.task('move-js', function(){

        return gulp.src('./src/js/libs/*.js')
            .pipe(gulp.dest('./dist/js/libs'));

    });

     // Move HTML Files To Built Directory

    gulp.task('move-html', function(){

        return gulp.src('./src/*.html')
            .pipe(gulp.dest('./dist'));

    });

     // Move Favicons To Built Directory

    gulp.task('move-icons', function(){

        return gulp.src(['./src/*.ico', './src/apple-touch-icon.png'])
            .pipe(gulp.dest('./dist'));

    });

    // Move SVG

    gulp.task('move-svg', function(){

        return gulp.src('./src/img/**/*.svg')
            .pipe(gulp.dest('./dist/img'));

    });

     // Move Miscellaneous Project Files To Built Directory

    gulp.task('move-misc', function(){

        return gulp.src('./src/.htaccess')
            .pipe(gulp.dest('./dist'));

    });

};