module.exports = function(gulp, plugins, config) {

    gulp.task('watch', function () {

        // Builds JavaScript

        plugins.watch(config.jsSrcPath + '/**/*.js', function () {
            gulp.start('js-build');
        });

        // Builds CSS

        plugins.watch(config.scssSrcPath + '/**/*.scss', function () {
            gulp.start('css-build');
        });

        // Optimizes Images

        plugins.watch([config.imgSrcPath + '/**/*.jpg', config.imgSrcPath + '/**/*.png', config.imgSrcPath + '/**/*.svg'], function () {
            gulp.start('imagemin');
        });

        // Copies Over HTML Files

        plugins.watch(['./src/*.html'], function () {
            gulp.start('html-build');
        });

        // Copies Over Favicons

        plugins.watch(['./src/apple-touch-icon.png', './src/js/libs/*.js'], function () {
            gulp.start('move-icons');
        });

        // Copies Over Miscellaneous Project Files

        plugins.watch(['./src/.htaccess'], function () {
            gulp.start('move-misc');
        });

    });

};

